<template>
    <div>
        <h4> create collection </h4>
        <form @submit.prevent="handleSubmit">
            <div class="form-group">
                <label for="name">Collection Name*</label>
                <input type="text" v-model="collection.name" v-validate="'required'" 
                name="name" class="form-control" :class="{ 'is-invalid': submitted && errors.has('name') }" />
                <div v-if="submitted && errors.has('name')" class="invalid-feedback">{{ errors.first('name') }}</div>
            </div>
            <div class="form-group">
                <label for="intro">Introduction</label>
                <textarea type="text" v-model="collection.intro" name="intro"></textarea>
            </div>
            <div class="form-group">
                <button class="btn btn-primary" :disabled="status.updating">Add</button>
                <img v-show="status.registering" src="data:image/gif;base64," />
            </div>
        </form>
    </div>
</template>

<script>
import { mapState, mapActions } from 'vuex'

export default {
    data () {
        return {
            collection: {
                name: '',
                intro: '',
            },
            submitted: false
        }
    },
    computed: {
        ...mapState('account', ['status','user'])
    },
    methods: {
        ...mapActions('account', ['addCollection']),
        handleSubmit(e) {
            this.submitted = true;
            this.$validator.validate().then(valid => {
                if (valid) {
                    this.addCollection({id:this.user._id, collection:this.collection});
                }
            });
        }
    }
}
</script>
